'''输入一棵二叉树，判断该二叉树是否是平衡二叉树。'''# -*- coding:utf-8 -*-# class TreeNode:#     def __init__(self, x):#         self.val = x#         self.left = None#         self.right = Noneclass Solution:    def IsBalanced_Solution(self, pRoot):        if pRoot == None:            return True        return self._is_balance(pRoot)[0]        pass    def _is_balance(self, p_root):        if p_root == None:            return (True, 0)        left = self._is_balance(p_root.left)        right = self._is_balance(p_root.right)        if (left[0] and right[0]):            diff = abs(right[1] - left[1])            if diff <= 1:                return (True, 1 + (left[1] if left[1] >= right[1] else right[1]))            pass        return (False, -1)        pass    pass