'''统计一个数字在排序数组中出现的次数。'''# -*- coding:utf-8 -*-class Solution:    def GetNumberOfK(self, data, k):        if data == None:            return None        length = len(data)        first = self._get_firt_k(data, k, 0, length - 1)        last = self.__get_last_k(data, k, 0, length - 1)        if first != -1 and last != -1:            return last - first + 1        return 0        pass    def _get_firt_k(self, data, k, start, end):        if start > end:            return -1        middle_index = (end + start) // 2        middle_data = data[middle_index]        if middle_data == k:            if (middle_index > 0 and data[middle_index - 1] != k) or middle_index == 0:                return middle_index            else:                end = middle_index - 1        elif middle_data > k:            end = middle_index - 1        else:            start = middle_index + 1        return self._get_firt_k(data, k, start, end)        pass    def __get_last_k(self, data, k, start, end):        if start > end:            return -1        middle_index = (end + start) // 2        middle_data = data[middle_index]        if middle_data == k:            if (middle_index < len(data) - 1 and data[middle_index + 1] != k) or middle_index == len(data) - 1:                return middle_index            else:                start = middle_index + 1        elif middle_data > k:            end = middle_index - 1        else:            start = middle_index + 1        return self.__get_last_k(data, k, start, end)        pass    passif __name__ == '__main__':    Solution().GetNumberOfK([1,2,3,3,3,3,4,5],3)