'''输入n个整数，找出其中最小的K个数。例如输入4,5,1,6,2,7,3,8这8个数字，则最小的4个数字是1,2,3,4,。'''# -*- coding:utf-8 -*-class Solution:    def GetLeastNumbers_Solution(self, tinput, k):        import bisect        if tinput == None or len(tinput) < k or k == 0:            return []        result = []        for i in range(k):            result.append(tinput[i])        result.sort()        for input in tinput[k:]:            if input < result[-1]:                result.pop(-1)                bisect.insort(result, input)                pass            pass        return result        pass    passif __name__ == '__main__':    Solution().GetLeastNumbers_Solution([4,5,1,6,2,7,3,8],4)