'''输入一个链表，反转链表后，输出链表的所有元素。'''# -*- coding:utf-8 -*-class ListNode:    def __init__(self, x):        self.val = x        self.next = Noneclass Solution:    # 返回ListNode    def ReverseList(self, pHead):        if pHead == None or pHead.next == None:            return pHead        # 插入头结点        new_head = pHead        pHead = pHead.next        new_head.next = None        # 头插法插入其他节点        while pHead != None:            next = pHead.next            pHead.next = new_head            new_head = pHead            pHead = next        return new_head    pass